package com.briup.java_day17.exercise.exercise2;

public class Printer extends Thread{
	@Override
	public void run(){
		while (true) {
			System.out.println(Storage.num);
			try {
				sleep(1000);
			} catch (InterruptedException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
		}
	}
}
